import {Picker,TextInput, Button, Image, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import React, { useEffect, useState } from 'react';
export default function WelcomeScreen({ navigation }){
    const [selectedValue, setSelectedValue] = useState("java");
    const [isFocus, setFocus] = useState(false)
    return(
        <View style={styles.container}>
            <View style={styles.form}>           
                <TouchableOpacity activeOpacity={0.6} onPress={() => navigation.navigate('Garbage') } >
                        <View>
                            <Text>Garbage</Text>
                        </View>
                </TouchableOpacity>
                <TextInput
                        onFocus={() => {
                            setFocus(true)
                        }}
                        onBlur={() => {
                            setFocus(false)
                        }}
                        style={(isFocus) ? (styles.inputFocus) : (styles.input) }
                        // onChangeText={onChangeNumber}
                        // value={number}
                        placeholder="Enter Password Connect"
                        // keyboardType="numeric"
                        />
                <Picker
                    selectedValue={selectedValue}
                    style={{ height: 50, width: 170 }}
                    onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
                >
                <Picker.Item label="Java" value="java" />
                <Picker.Item label="JavaScript" value="js" />
                </Picker>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#f8f8ff',
      position:"relative",
      paddingVertical:200,
      paddingHorizontal:50
    },
    input:{
        height: 40,
        margin: 12,
        padding: 5,
        borderWidth: 1,
        borderRadius:10,
        borderColor:"#0000cd",
        shadowColor:"#0000cd",
        shadowRadius:5,
        shadowOpacity:0.6
    },
    inputFocus:{
        height: 40,
        margin: 12,
        padding: 5,
        borderWidth: 1,
        borderRadius:10,
        outline:0,
        borderColor:"none"
    }
    ,
    picker:{
        width:200
    },
    form:{
        backgroundColor: '#fff',
        flexDirection:"column",
        justifyContent:"center",
        alignItems:"center",
        paddingVertical:20,
        borderRadius:5
    }
});
